snippet::array-last.example:
const last = (arr) => arr.at(-1);
// or in older environments
const last = (arr) => arr[arr.length - 1]

snippet::base64.example:
// From base64 to Uint8Array
const bytes = Uint8Array.fromBase64(base64)
// From Uint8Array to base64
const base64 = bytes.toBase64()

snippet::is-arraybuffer.example:
const isArrayBuffer = obj instanceof ArrayBuffer;
// for cross-realm
const isArrayBufferCrossRealm = Object.prototype.toString.call(obj) === "[object ArrayBuffer]"

snippet::is-date.example:
const isDate = v instanceof Date;
// for cross-realm
const isDateCrossRealm = Object.prototype.toString.call(v) === "[object Date]"

snippet::is-regexp.example:
const isRegExp = (v) => v instanceof RegExp;
// for cross-realm
const isRegExpCrossRealm = Object.prototype.toString.call(v) === "[object RegExp]";

snippet::typeof.example:
const typeOf = (value) => typeof value;
// for more specific types
const classOf = (value) => Object.prototype.toString.call(value);
